import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { loginUser } from '../utils/authUtils';

const Login = ({ setUser }) => {
  const [form, setForm] = useState({ username: '', password: '' });
  const [error, setError] = useState('');
  const history = useHistory();

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleLogin = async e => {
    e.preventDefault();
    setError('');
    try {
      const u = await loginUser(form);
      setUser(u);
      history.push('/dashboard');
    } catch (err) {
      setError('Login failed');
    }
  };

  // OAuth login handler
  const handleOAuthLogin = () => {
    window.location.href = 'http://localhost:5000/api/auth/google'; // Backend OAuth endpoint
  };

  return (
    <div style={{ maxWidth: 400, margin: '2rem auto', padding: '2rem', border: '1px solid #ccc' }}>
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          name="username"
          placeholder="Username"
          value={form.username}
          onChange={handleChange}
          required
          style={{ display: 'block', marginBottom: 8, width: '100%' }}
        />
        <input
          name="password"
          type="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          required
          style={{ display: 'block', marginBottom: 8, width: '100%' }}
        />
        <button type="submit">Login (JWT)</button>
      </form>
      <button onClick={handleOAuthLogin} style={{ marginTop: 8 }}>
        Login with Google (OAuth)
      </button>
      {error && <div style={{ color: 'red', marginTop: 8 }}>{error}</div>}
    </div>
  );
};

export default Login;
