import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { loginUser, registerUser } from '../utils/authUtils';

const AuthPage = ({ setUser }) => {
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({ username: '', password: '', role: 'user' });
  const [error, setError] = useState('');
  const history = useHistory();

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleLogin = async e => {
    e.preventDefault();
    setError('');
    try {
      const u = await loginUser(form);
      setUser(u);
      history.push('/dashboard');
    } catch (err) {
      setError('Login failed');
    }
  };

  const handleRegister = async e => {
    e.preventDefault();
    setError('');
    try {
      await registerUser(form);
      setIsLogin(true);
    } catch (err) {
      setError('Registration failed');
    }
  };

  // OAuth login handler
  const handleOAuthLogin = () => {
    window.location.href = 'http://localhost:5000/api/auth/google'; // Backend OAuth endpoint
  };

  // Handle token in URL after OAuth
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('token');
    if (token) {
      localStorage.setItem('jwt', token); // Store token
      // Optionally, fetch user info using the token
      setUser({ token }); // Or fetch user details from backend
      window.location.href = '/dashboard'; // Redirect to dashboard
    }
  }, [setUser]);

  return (
    <div>
      <h1>AuthPage Loaded</h1>
      <div style={{ maxWidth: 400, margin: '2rem auto', padding: '2rem', border: '1px solid #ccc' }}>
        <h2>{isLogin ? 'Login' : 'Register'}</h2>
        <form onSubmit={isLogin ? handleLogin : handleRegister}>
          <input
            name="username"
            placeholder="Username"
            value={form.username}
            onChange={handleChange}
            required
            style={{ display: 'block', marginBottom: 8, width: '100%' }}
          />
          <input
            name="password"
            type="password"
            placeholder="Password"
            value={form.password}
            onChange={handleChange}
            required
            style={{ display: 'block', marginBottom: 8, width: '100%' }}
          />
          {!isLogin && (
            <select
              name="role"
              value={form.role}
              onChange={handleChange}
              style={{ display: 'block', marginBottom: 8, width: '100%' }}
            >
              <option value="user">User</option>
              <option value="admin">Admin</option>
            </select>
          )}
          <button type="submit">{isLogin ? 'Login (JWT)' : 'Register'}</button>
        </form>
        <button onClick={handleOAuthLogin} style={{ marginTop: 8 }}>
          Login with Google (OAuth)
        </button>
        <div style={{ marginTop: 8 }}>
          <button onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? 'Create an account' : 'Back to login'}
          </button>
        </div>
        {error && <div style={{ color: 'red', marginTop: 8 }}>{error}</div>}
      </div>
    </div>
  );
};

export default AuthPage;
