import React, { useEffect } from 'react';
import { getCurrentUser } from '../utils/authUtils';

const Dashboard = ({ user, setUser, onLogout }) => {
  useEffect(() => {
    if (!user) {
      getCurrentUser().then(u => {
        if (u) setUser(u);
      });
    }
  }, [user, setUser]);

  if (!user) return <div>Loading...</div>;

  return (
    <div style={{ maxWidth: 600, margin: '2rem auto', padding: '2rem', border: '1px solid #ccc' }}>
      <h2>Dashboard</h2>
      <p>Welcome, <strong>{user.username}</strong>!</p>
      <p>Your role: <strong>{user.role}</strong></p>
      <button onClick={onLogout} style={{ marginBottom: 16 }}>Logout</button>
      {user.role === 'admin' ? (
        <div>
          <h3>Admin Panel</h3>
          <p>You have access to admin features.</p>
        </div>
      ) : (
        <div>
          <h3>User Panel</h3>
          <p>You have access to user features.</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
