import { useState, useEffect } from 'react';

export default function useAuth() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5000/api/me', {
      credentials: 'include',
    })
      .then(res => res.json())
      .then(data => setUser(data.user || null));
  }, []);

  return user;
}
